library(qualV)
library(stringr)

string1 <- ""
string2 <- ""

start_time <- Sys.time()

string1 <- as.vector(str_split_fixed(string1, pattern = "", n = nchar(string1)))
string2 <- as.vector(str_split_fixed(string2, pattern = "", n = nchar(string2)))

lcs <- LCS(string1, string2)

end_time <- Sys.time()

paste(lcs$LCS,collapse="")
print(end_time - start_time)


